import React, { useState } from "react";

export default function App() {
  const listStyle = { padding: "10px", borderBottom: "1px #ccc dotted", textDecoration: "none" };

  const btnStyle = { color: "#fff", border: "none", padding: "5px 9px", borderRadius: "50%", cursor: "pointer", float: "right" };

  // 할일목록 배열에 저장
  const todoList = [
    { id: "1", title: "공부하기", completed: true },
    { id: "2", title: "청소하기", completed: false },
  ];

  //2. textChange 함수
  const textChange = (e) => {
    // console.log("e", e.target.value);
    setvalue(e.target.value);
  };

  const [value, setValue] = useState("");

  return (
    <div className="container">
      <div className="todoBlock">
        <div className="title">
          <h1>To Do List</h1>
        </div>
        {/*2.map()메소드를 이용해 배열내에 요소를 호출한 결과를 모아 새로운 배열로 반환 */}
        {todoList.map((data) => (
          <div style={listStyle} key={data.id}>
            <input type="checkbox" defaultChecked={false} />
            {data.title}
            <button style={btnStyle}>X</button>
          </div>
        ))}
        {/* 할일목록 추가 입력창 만들기 */}
        <form style={{ display: "flex" }}>
          <input type="text" name="value" style={{ flex: "10", padding: "5px" }} placeholder="해야할 일을 입력하세요" onChange={textChange} value="" />
        </form>
      </div>
    </div>
  );
}
